SELECT
   0 s_0,
   CASE WHEN UPPER("E-Inventory"."Bibliographic Details"."Network Number") LIKE '%OCOLC%' THEN REPLACE(LEFT(SUBSTRING("E-Inventory"."Bibliographic Details"."Network Number" FROM LOCATE('(OCOLC',UPPER("E-Inventory"."Bibliographic Details"."Network Number"))),LOCATE(' ',SUBSTRING(CONCAT("E-Inventory"."Bibliographic Details"."Network Number",'; ') FROM LOCATE('(OCOLC',UPPER("E-Inventory"."Bibliographic Details"."Network Number"))))),';','') WHEN UPPER("E-Inventory"."Bibliographic Details"."Network Number") LIKE '%OCM%' THEN REPLACE(LEFT(SUBSTRING("E-Inventory"."Bibliographic Details"."Network Number" FROM LOCATE('OCM',UPPER("E-Inventory"."Bibliographic Details"."Network Number"))),LOCATE(' ',SUBSTRING(CONCAT("E-Inventory"."Bibliographic Details"."Network Number",'; ') FROM LOCATE('OCM',UPPER("E-Inventory"."Bibliographic Details"."Network Number"))))),';','') WHEN UPPER("E-Inventory"."Bibliographic Details"."Network Number") LIKE '%OCN%' THEN REPLACE(LEFT(SUBSTRING("E-Inventory"."Bibliographic Details"."Network Number" FROM LOCATE('OCN',UPPER("E-Inventory"."Bibliographic Details"."Network Number"))),LOCATE(' ',SUBSTRING(CONCAT("E-Inventory"."Bibliographic Details"."Network Number",'; ') FROM LOCATE('OCN',UPPER("E-Inventory"."Bibliographic Details"."Network Number"))))),';','') WHEN UPPER("E-Inventory"."Bibliographic Details"."Network Number") LIKE '%ON%' THEN REPLACE(LEFT(SUBSTRING("E-Inventory"."Bibliographic Details"."Network Number" FROM LOCATE('ON',UPPER("E-Inventory"."Bibliographic Details"."Network Number"))),LOCATE(' ',SUBSTRING(CONCAT("E-Inventory"."Bibliographic Details"."Network Number",'; ') FROM LOCATE('ON',UPPER("E-Inventory"."Bibliographic Details"."Network Number"))))),';','') ELSE 'No OCLC Number Available' END s_1
FROM "E-Inventory"
WHERE
(("Bibliographic Details"."Bibliographic Lifecycle" = 'Deleted') AND ("Bibliographic Details"."Material Type" <> 'Journal') AND ("Bibliographic Details"."Modification Date" >= (TIMESTAMPADD(SQL_TSI_DAY, -1, CURRENT_DATE))))
